const test = require("tape").test

test("test w/ plain node", function (t) {
  if (~process.execArgv.indexOf("--harmony")
  || require("path").parse(process.execPath).name === "iojs") {
    t.equal(!!require("../index"), true, "supported via harmony or iojs")
  } else {
    t.equal(!!require("../index"), false, "not supported")
  }
  t.end()
})
